const rawData = items[0].json.data;

// Paso 1: Separar por líneas
const lines = rawData.split('\n');

// Paso 2: Parsear cada línea como JSON
let responses = [];
for (const line of lines) {
  try {
    const parsed = JSON.parse(line);
    if (parsed.response) {
      responses.push(parsed.response);
    }
  } catch (e) {
    // Ignorar líneas que no se puedan parsear
  }
}

// Paso 3: Concatenar todas las respuestas
const textoFinal = responses.join('');

// Devolver el resultado
return [
  {
    json: {
      mensaje_qwen: textoFinal
    }
  }
];

// Tomamos directamente el mensaje normalizado en inglés
const mensaje_ingles = $input.first().json.mensaje_qwen || "";

// Construimos el prompt para pedir la traducción al español
const prompt_traduccion = `
Tomá el siguiente texto técnico en inglés, que contiene un análisis de logs de red,
y traducilo al español. Mantené todo el contenido técnico, la estructura y la claridad profesional.
No modifiques los datos ni agregues información adicional, solo traducilo.

Texto a traducir:
${mensaje_ingles}
`;

// Construimos el JSON que se enviará al nodo HTTP Request de Qwen
const body_json = {
    model: "qwen2.5-coder:14b",
    prompt: prompt_traduccion
};

return [
    {
        json: {
            body_json: body_json
        }
    }
];

const rawData = items[0].json.data;

// Separar por líneas y parsear JSON
const responses = rawData
  .split('\n')
  .map(line => {
    try {
      const parsed = JSON.parse(line);
      return parsed.response || '';
    } catch {
      return '';
    }
  })
  .filter(Boolean);

// Concatenar todos los fragments directamente para reconstruir palabras completas
const textoReconstruido = responses.join('');

// (Opcional) insertar saltos de línea donde haya dos o más espacios consecutivos
// para separar párrafos, listas, etc.
const textoFinal = textoReconstruido.replace(/  +/g, '\n\n');

// Construir JSON para Teams
const mensajeTeams = {
  "@type": "MessageCard",
  "@context": "http://schema.org/extensions",
  "summary": "Informe de logs de red",
  "themeColor": "0076D7",
  "title": "Análisis de logs de red",
  "text": textoFinal
};

return [
  {
    json: mensajeTeams
  }
];
