const items = $input.all();

const resumen = {};

for (const item of items) {
  const sysLocation = item.json.sysLocation || "";
  if (!sysLocation.includes("ME-")) continue;

  const region = sysLocation.split('_')[0]; // Ej: "ME-NJUL"
  const criticidad = (item.json.criticidad_servicio?.trim() || "Desconocida");
  const tipo = (item.json.tipo_cliente?.trim() || "Desconocido");
  const cliente = (item.json.Cliente?.trim() || "SinNombre");

  if (!resumen[region]) {
    resumen[region] = {
      region_MST: region,
      total_servicios: 0,
      clientes: new Set(),
      // Aquí irán directamente los tipos de cliente como claves
      // Ej: "PREFER": { "No Critico": 100, ... }
    };
  }

  const r = resumen[region];
  r.total_servicios += 1;
  r.clientes.add(cliente);

  // Inicializar el tipo de cliente si no existe
  if (!r[tipo]) {
    r[tipo] = {};
  }

  // Contar la criticidad dentro de ese tipo de cliente
  r[tipo][criticidad] = (r[tipo][criticidad] || 0) + 1;
}

// Convertir a salida final: reemplazar el Set por el conteo de clientes únicos
const salida = Object.values(resumen).map(r => {
  const { clientes, ...rest } = r; // extraemos 'clientes' del objeto
  return {
    region_MST: r.region_MST,
    total_servicios: r.total_servicios,
    clientes_unicos: r.clientes.size,
    ...rest // esto incluye "PREFER", "Empresas y Pymes", etc.
  };
});

return salida.map(s => ({ json: s }));

const allInputs = $input.all();

// Separar los datos
const regionesItems = [];
const pesosTipoItems = [];
const pesosCritItems = [];

for (const item of allInputs) {
  if (item.json.origen === 'tipo_cliente') {
    pesosTipoItems.push(item);
  } else if (item.json.origen === 'criticidad') {
    pesosCritItems.push(item);
  } else {
    regionesItems.push(item);
  }
}

// Convertir pesos a objetos para acceso rápido
const pesosTipo = {};
for (const item of pesosTipoItems) {
  pesosTipo[item.json.tipo_cliente] = item.json.peso;
}

const pesosCrit = {};
for (const item of pesosCritItems) {
  pesosCrit[item.json.criticidad_servicio] = item.json.peso;
}

// Procesar cada región y calcular puntajes (con SUMA, como en tu versión que funcionaba)
const regionesConPuntaje = [];

for (const regionItem of regionesItems) {
  const r = regionItem.json;
  const { region_MST, total_servicios, clientes_unicos, ...tipos } = r;

  let puntaje_total_region = 0;
  const detalle_por_tipo = {};

  for (const [tipoCliente, criticidades] of Object.entries(tipos)) {
    let puntaje_tipo = 0;

    for (const [criticidad, cantidad] of Object.entries(criticidades)) {
      const pesoTipo = pesosTipo[tipoCliente] ?? 0;
      const pesoCrit = pesosCrit[criticidad] ?? 0;
      // ✅ Mantenemos la SUMA (tu lógica original que funcionaba)
      const puntajeItem = (pesoTipo + pesoCrit) * cantidad;

      puntaje_tipo += puntajeItem;
      puntaje_total_region += puntajeItem;
    }

    detalle_por_tipo[tipoCliente] = puntaje_tipo;
  }

  regionesConPuntaje.push({
    region_MST,
    total_servicios,
    clientes_unicos,
    puntaje_total: puntaje_total_region,
    puntaje_por_tipo: detalle_por_tipo
  });
}

// ✅ Normalización MIN-MAX: mejor = 100, peor = 0
const puntajes = regionesConPuntaje.map(r => r.puntaje_total);
const max = Math.max(...puntajes);
const min = Math.min(...puntajes);

const resultado = regionesConPuntaje.map(r => {
  let normalizado;
  if (max === min) {
    // Si todos iguales: si son 0 → 0, si son >0 → 100
    normalizado = max === 0 ? 0 : 100;
  } else {
    normalizado = ((r.puntaje_total - min) / (max - min)) * 100;
  }

  return {
    region_MST: r.region_MST,
    total_servicios: r.total_servicios,
    clientes_unicos: r.clientes_unicos,
    puntaje_total: r.puntaje_total,
    puntaje_total: Math.round(normalizado * 100) / 100,
    puntaje_por_tipo: r.puntaje_por_tipo
  };
});

return resultado.map(r => ({ json: r }));

// Obtener todos los valores de traffic_30d
const trafficValues = items.map(item => item.json.traffic_30d || 0);

// Encontrar min y max
const minTraffic = Math.min(...trafficValues);
const maxTraffic = Math.max(...trafficValues);
const range = maxTraffic - minTraffic || 1;

// Normalizar y devolver los campos necesarios + tráfico en Gb
return items.map(item => {
  const trafficBytes = item.json.traffic_30d || 0;

  // Convertir a gigabits (Gb): bytes → bits → Gb
  const traffic_Gb = (trafficBytes / 1e9);

  // Calcular score
  const traffic_score = ((trafficBytes - minTraffic) / range) * 100;

  return {
    json: {
      Acronimo: item.json.Acronimo,
      Equipo: item.json.Equipo,
      Region_MST: item.json.Region_MST,
      traffic_score: parseFloat(traffic_score.toFixed(2)),
      traffic_Gb: parseFloat(traffic_Gb.toFixed(2)) // tráfico en gigabits
    }
  };
});

const allItems = $input.all();

const datosTrafico = [];
const datosPuntaje = [];

for (const item of allItems) {
  if (item.json.Acronimo !== undefined) {
    datosTrafico.push(item.json);
  } else if (item.json.puntaje_total !== undefined) {
    datosPuntaje.push(item.json);
  }
}

// Mapa de puntaje por región
const puntajeMap = {};
for (const p of datosPuntaje) {
  const key = (p.region_MST || '').toString().trim().toUpperCase();
  puntajeMap[key] = p;
}

// Lista de tipos de cliente conocidos (ajusta según tu realidad)
const tiposConocidos = [
  "PREFER",
  "Sin asignar",
  "Desconocido",
  "Corporativo y linea de vida",
  "Grandes Clientes y Carriers",
  "Empresas y Pymes"
];

// Función para normalizar nombre de tipo a columna SQL
function normalizarTipo(tipo) {
  return "tipo_" + tipo
    .replace(/ y /g, "_y_")
    .replace(/\s+/g, "_")
    .replace(/[^a-zA-Z0-9_]/g, "")
    .replace(/_{2,}/g, "_")
    .replace(/^_+|_+$/g, "");
}

const resultado = [];

for (const t of datosTrafico) {
  const regionKey = (t.Region_MST || '').toString().trim().toUpperCase();
  const puntajeData = puntajeMap[regionKey] || {
    puntaje_total: 0,
    total_servicios: 0,
    clientes_unicos: 0,
    puntaje_por_tipo: {}
  };

  // Renombrar y calcular
  const puntaje_trafico = t.traffic_score || 0;
  const puntaje_region = puntajeData.puntaje_total || 0;
  const puntaje_ranking = (0.3 * puntaje_trafico) + (0.7 * Math.min(puntaje_region, 100));

  // Objeto base con nombres actualizados
  const salida = {
    Acronimo: t.Acronimo,
    Equipo: t.Equipo,
    Region_MST: t.Region_MST,
    traffic_Gb: t.traffic_Gb,
    puntaje_trafico: parseFloat(puntaje_trafico.toFixed(2)), // ✅ renombrado
    total_servicios: puntajeData.total_servicios || 0,
    clientes_unicos: puntajeData.clientes_unicos || 0,
    puntaje_region: puntaje_region, // ✅ renombrado
    puntaje_ranking: parseFloat(puntaje_ranking.toFixed(2))
  };

  // Inicializar todos los tipos en 0
  for (const tipo of tiposConocidos) {
    const colName = normalizarTipo(tipo);
    salida[colName] = 0;
  }

  // Asignar valores reales
  const puntajePorTipo = puntajeData.puntaje_por_tipo || {};
  for (const [tipo, valor] of Object.entries(puntajePorTipo)) {
    const colName = normalizarTipo(tipo);
    salida[colName] = valor || 0;
  }

  resultado.push({ json: salida });
}

return resultado;
