// FunciÃ³n para n8n - Transforma array en lista plana de tareas
function transformWorkflowData(inputArray) {
    const tareas = [];

    // Procesar cada item del input
    inputArray.forEach(item => {
        const tareaItem = {
            wbs: item.wbs || '',
            form: item.form || '',
            tas: item.tas || '',
            estado: item.estado || '',
            prioridad: extractPrioridad(item.proyecto),
            tipo: extractTipo(item.proyecto),
            sitio: item.sitio || '',
            fecha: item.fecha || '',
            hora: extractHora(item.hora_de_plan_de_inicio, item.Hora_inicio), // âœ… Usar la nueva funciÃ³n
            id: item.id || '',
            url: item.url || '',
            contratista: item.contratista || '',
            proyecto: item.proyecto || '',
            tarea: sanitizeTarea(item.tarea),   // âœ… usar la funciÃ³n de limpieza aquÃ­
            ejecutante: item.ejecutante || '',
            afecta_clientes: item.afecta_clientes || '',

            // Nuevo campo calculado
            categoria: extractCategoria(item.ejecutante)
        };

        tareas.push(tareaItem);
    });

    return tareas; // Devuelve un array plano
}

// ðŸ§¹ FunciÃ³n auxiliar: reemplazar "\" o caracteres de control como \u000b por "-"
function sanitizeTarea(tarea) {
    if (!tarea) return '';
    return tarea.replace(/[\u000b\\]/g, '-');  // Reemplaza \u000b y "\" por guion
}

// FunciÃ³n auxiliar: Extraer prioridad del nombre del proyecto
function extractPrioridad(proyecto) {
    if (!proyecto) return '';

    const proyectoUpper = proyecto.toUpperCase();

    if (proyectoUpper.includes('EMERGENCIA')) {
        return 'EMERGENCIA';
    } else if (proyectoUpper.includes('NO CRÃTICO') || proyectoUpper.includes('NO CRITICO')) {
        return 'NO CRÃTICO';
    } else if (proyectoUpper.includes('CRÃTICO') || proyectoUpper.includes('CRITICO')) {
        return 'CRÃTICO';
    }

    return '';
}

// FunciÃ³n auxiliar: Determinar tipo segÃºn nombre del proyecto
function extractTipo(proyecto) {
    if (!proyecto) return 'ValidaciÃ³n';

    if (proyecto.trim().toUpperCase().startsWith('SAE')) {
        return 'EjecuciÃ³n';
    }

    return 'ValidaciÃ³n';
}

// ðŸ•’ FunciÃ³n auxiliar: Extraer hora segÃºn la nueva lÃ³gica
function extractHora(hora_de_plan_de_inicio, Hora_inicio) {
    // Si "hora_de_plan_de_inicio" estÃ¡ vacÃ­o o es null, usar "Hora_inicio"
    if (!hora_de_plan_de_inicio) {
        if (Hora_inicio) {
            // Formatear el valor de Hora_inicio a "hh:mm hs."
            const cleanHora = Hora_inicio.toString().replace(/\s*hs\.?\s*$/, '').trim();
            const [hours, minutes] = cleanHora.split(':').map(part => parseInt(part, 10));

            if (!isNaN(hours) && !isNaN(minutes)) {
                const formattedHours = hours.toString().padStart(2, '0');
                const formattedMinutes = minutes.toString().padStart(2, '0');
                return `${formattedHours}:${formattedMinutes} hs.`;
            }
        }
        return ''; // Si ambos estÃ¡n vacÃ­os, devolver cadena vacÃ­a
    }

    // Si "hora_de_plan_de_inicio" tiene valor, formatearlo a "hh:mm hs."
    const cleanHoraPlan = hora_de_plan_de_inicio.toString().replace(/\s*hs\.?\s*$/, '').trim();
    const [hours, minutes] = cleanHoraPlan.split(':').map(part => parseInt(part, 10));

    if (!isNaN(hours) && !isNaN(minutes)) {
        const formattedHours = hours.toString().padStart(2, '0');
        const formattedMinutes = minutes.toString().padStart(2, '0');
        return `${formattedHours}:${formattedMinutes} hs.`;
    }

    // Si no se puede formatear, devolver el valor original
    return hora_de_plan_de_inicio;
}

// ðŸš€ Nueva funciÃ³n auxiliar: Determinar categorÃ­a segÃºn el ejecutante
function extractCategoria(ejecutante) {
    if (!ejecutante || ejecutante.trim() === '') {
        return 'ValidaciÃ³n';
    }

    const ejecutanteTrim = ejecutante.trim();

    if (ejecutanteTrim === 'Soporte Acceso Empresas - SAE') {
        return 'SAE';
    } else if (
        ejecutanteTrim === 'Proveedor Externo' ||
        ejecutanteTrim === 'ImplementaciÃ³n de Servicios de Acceso - ISA'
    ) {
        return 'Terceros';
    }

    return '';
}

