// Recorre cada elemento de entrada
const tareas = [];

for (const item of $input.all()) {
  const t = item.json;

  // Normaliza valores por seguridad
  const accionSAE = t.accion_sae?.trim().toLowerCase();
  const ejecutante = t.ejecutante?.trim().toLowerCase();

  // Lógica de negocio para el nuevo campo "aprobar"
  if (accionSAE !== "si" && ejecutante !== "soporte acceso empresas - sae") {
    t.aprobar = "Ingeniero";
  } else {
    t.aprobar = "Especialista";
  }

  // Nueva lógica: asignar nombre de proyecto según el código
  switch (t.codigo_proyecto) {
    case "PR-23-0279":
      t.proyecto = "SAE-CRÍTICO-IMPLEMENTACIONES ISA";
      break;
    case "PR-23-0996":
      t.proyecto = "SAE-NO CRÍTICO-IMPLEMENTACIONES ISA";
      break;
    case "PR-23-0343":
      t.proyecto = "SAE-CRÍTICO-CONFIGURACION/UPGRADES";
      break;
    case "PR-23-0344":
      t.proyecto = "SAE-NO CRÍTICO-CONFIGURACION/UPGRADES";
      break;
    case "PR-23-0345":
      t.proyecto = "SAE-EMERGENCIA DE RED";
      break;
    default:
      t.proyecto = "SIN CLASIFICAR";
  }

  // Agrega el resultado al arreglo de tareas
  tareas.push({
    wbs: t.wbs,
    form: t.form,
    estado: t.estado,
    tarea: t.tarea,
    ejecutante: t.ejecutante,
    afecta_clientes: t.afecta_clientes,
    hora_inicio: t.hora_inicio,
    requirente: t.requirente,
    proveedor: t.proveedor,
    fecha_estimada: t.fecha_estimada,
    accion_sae: t.accion_sae,
    codigo_proyecto: t.codigo_proyecto,
    proyecto: t.proyecto,
    aprobar: t.aprobar,
    fecha_creacion: t.fecha_creacion,
    nombre_creador: t.nombre_creador,
    url: t.url
  });
}

// Devuelve la salida en el formato solicitado
return [
  {
    json: {
      tareas: tareas
    }
  }
];
